public static DocumentRoutingJob CreateNewDocumentRoutingJob(str blobItemName,  str printerName, str printerPath, str documentName, guid activityId, int totalPages, Guid printerId, CompanyId companyId, UserConnection uc)
{
    DocumentRoutingJob documentRoutingJob;

    documentRoutingJob = next CreateNewDocumentRoutingJob(blobItemName, printerName, printerPath, documentName, activityId, totalPages, printerId, companyId, uc);

    WHSSysCorpNetPrinters whsPrinter = WHSSysCorpNetPrinters::find(printerName);
    if (whsPrinter.LabelPrintServiceExecutionPolicy == WhsLabelPrintServiceExecutionPolicy::PAL29ZPLPrintManagement)
    {
        documentRoutingJob.EndProcessTime = DateTimeUtil::utcNow();
        documentRoutingJob.StartProcessTime = DateTimeUtil::utcNow();
        documentRoutingJob.JobStatus = DocumentRoutingJobStatus::Succeeded;
        documentRoutingJob.update();
    }

    return documentRoutingJob;
}
