public static void printLabelPrintCommandToPrinterWithEncoding(
    Name _printerName,
    WhsLabelPrintCommand _labelPrintCommand,
    System.Text.Encoding _encoding)
{
    next printLabelPrintCommandToPrinterWithEncoding(_printerName, _labelPrintCommand, _encoding);

    SrsReportPrinterContract activePrinterContract = SrsReportRunUtil::getActivePrinter(_printerName);
    WHSSysCorpNetPrinters whsPrinter = WHSSysCorpNetPrinters::find(_printerName);
    if (whsPrinter.LabelPrintServiceExecutionPolicy == WhsLabelPrintServiceExecutionPolicy::PAL29ZPLPrintManagement)
    {
        ZPLPrintManagementHelper::handleZPLPrinterRequst(
            _printerName,
            _labelPrintCommand.parmLabelString(),
            whsPrinter.ZPLPrinterWidth_ALTC,
            whsPrinter.ZPLPrinterHeight_ALTC,
            whsPrinter.ZPLPrinterDPI);
    }
    else if (whsPrinter.ZPLPrinterDebug_ALTC)
    {
        ZPLPrintManagementHelper::debugZPLPrinterRequst(
            _printerName,
            _labelPrintCommand.parmLabelString());
    }
}
